@mixin displayGrid($gridGap, $gridTemplateAreas, $gridTemplateRows, $gridTemplateColumns) {
  display: grid;
  grid-gap: $gridGap;
  grid-template-areas: $gridTemplateAreas;
  grid-template-rows: $gridTemplateRows;
  grid-template-columns: $gridTemplateColumns;
}

@mixin displayFlex($alignItems, $justifyContent, $flexDirection) {
  display: flex;
  align-items: $alignItems;
  justify-content: $justifyContent;
  flex-direction: $flexDirection;
}

@mixin posWidHei($position, $width, $height) {
  position: $position;
  width: $width;
  height: $height;
}

@import "./_sideNav";

body {
  overflow: hidden;
  /* stop ability to scroll down */
  margin: 0px;
  background-color: #141414;
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  color: white;
}

a,
button,
.link {
  cursor: pointer;
}

.preloader, .changing {
  @include displayFlex(center, center, null);
  @include posWidHei(absolute, 100%, 100%);
  z-index: 1;
  background-color: #141414;
  overflow: hidden;
}

// only visible when loading new page
.changing {
  z-index: -10;
}

/* remove zoom on double tap */ 
.disable-dbl-tap-zoom {
  touch-action: manipulation;
}

@import "./_topContainer";
@import "./_mainContainer.scss";
@import "./_bottomContainer";
@import "./_mediaQueries";

sources.css/_topContainer.scss:
.background-image {
    @include posWidHei(absolute, 100%, 75%);
    z-index: -1;
    background-image: url(../imgs/bg-image.jpg);
    background-size: cover;
}

.top-container {
    @include displayGrid(3px, 'logo''tag-line''learn-more-btn', 0.4fr 0.3fr 0.3fr, null);
    height: 55vh;
    width: 100%;
    grid-auto-flow: dense;

    * {
        margin-left: 5%;
        margin-top: 3%;
    }
}

.logo {
    grid-area: logo;
    font-size: 2.5vw;
    font-weight: bold;
}

.kids {
    position: absolute;
    right: 50px;
    border: none;
    background-color: transparent;
    font-size: 1.1rem;
    text-transform: uppercase;
    color: white;
    font-weight: 600;
    &:hover {
        color: red;
    }
}

.tag-line {
    grid-area: tag-line;
    font-size: 4vw;
    align-self: end;
    margin-bottom: 15px;
}

.learn-more-btn {
    width: 11%;
    font-size: learn-more-btn;
    background-color: rgba(51, 51, 51, 0.5);
    border-width: 0;
    height: 40px;
    border-radius: .2vw;
    box-shadow: none;
    font-size: 1.3vw;
    font-weight: 700;
    color: white;
    margin-top: 0px;
    transition: 0.3s;

    &:hover {
        background-color: white;
        color: rgba(51, 51, 51, 1);
    }
}

sources.css/_mainContainer.scss:
.main-container {
    @include displayGrid(3px, 'left left-container    central-container    right-container right', null, 0.3fr 0.5fr 0.9fr 0.5fr 0.3fr);
    height: 40vh;
    width: 100%;
    grid-auto-flow: dense;
}

.empty-box-container {
    border-left: none;
}

.empty-box-container-left {
    grid-area: left;
}

.empty-box-container-right {
    grid-area: right;
}

.red {
    color: red;
}

.central-container {
    @include posWidHei(relative, 100%, 100%);
    grid-area: central-container;
    border: 1px solid black;

    > {
        .img {
            @include posWidHei(absolute, 100%, 100%);
            /* gradient overlay [ to make text more readable ] */
            background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 59%, rgba(0, 0, 0, 0.65) 100%), url('../imgs/basketball.jpg') no-repeat;
        }

        .activity-info {
            @include displayGrid(null, 'name''categories', 1fr 0.5fr, null);
            @include posWidHei(absolute, 101%, 100%);
            /* stop word from overflowing div - instead, break word + hyphenate */
            overflow-wrap: break-word;
            word-wrap: break-word;
            hyphens: auto;
            background: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 59%, rgba(0, 0, 0, 1) 100%);
        }
    }
}

.name {
    grid-area: name;
}

.categories {
    grid-area: categories;
    color: white;
    font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    align-self: end;
    /* bottom of area */
    position: relative;
    overflow: hidden;
    /* keep categories on page even when font is large */
}

.name,
.categories {
    margin: 3%;
}

.right-container,
.empty-box-container-left,
.empty-box-container-right,
.left-container {
    @include displayFlex(null, center, column);
}

.left-container {
    grid-area: left-container;
}

.right-box {
    @include displayFlex(null, center, null);
}

.left-box,
.right-box,
.empty-box-left,
.empty-box-right {
    width: 100%;
    height: 60%;
    background-size: cover;
}

.left-box {
    @include displayGrid(null, 'chill-level arrows', null, 1fr 0.5fr);
    background-image: url('../imgs/left-image.jpg');
}

.chill-level {
    grid-area: chill-level;
}

.chill-level-name {
    font-size: 2vw;
    margin-bottom: 0px;
    top: 3%;
    margin-top: 30%;
    line-height: 0.7;
}

.chill-level-amount {
    font-size: 2.4vw;
    margin-top: 0px;
    font-weight: bold;
}

.arrows {
    @include displayGrid(null, 'arrowUp''arrowDown', 1fr 1fr, null);
    grid-area: arrows;
    height: 100%;
    width: 100%;
    grid-row-gap: 10%;
    /* center horiztonally + vertically */
    justify-self: center;
    align-self: center;
}

.chill-level {
    /* center horiztonally + vertically */
    justify-self: center;
    align-self: center;
}

.up,
.down {
    width: 60%;
}

.up,
.down,
.refresh {
    height: auto;
    transition: all .2s;
    fill: white;
}

.up {
    grid-area: arrowUp;
    align-self: end;
}

.down {
    grid-area: arrowDown;
}

.right-container {
    grid-area: right-container;
}

.right-box {
    background-image: url('../imgs/beachChill.jpg');
}

.refresh {
    align-self: center;
    width: 20%;
}

.refresh:hover, .up:hover, .down:hover {
    fill: red;
  }

.empty-box {
    background-color: black;
}

.empty-box-right {
    background-image: url('../imgs/ferris-wheel.jpg');
}

.empty-box-left {
    background-image: url('../imgs/house.jpg');
}

